import React from "react";
import Auth from "./Auth.js";

function App() {
  const Auth = ({ onLogin }) => {
      const [isLogin, setIsLogin] = React.useState(true);
      const [formData, setFormData] = React.useState({
          username: "",
          email: "",
          password: ""
      });

    const handleSubmit = async (e) => {
        e.preventDefault();
        const endpoint = isLogin ? "/api/auth/login" : "/api/auth/register";

        try {
            const response = await fetch(`http://91.228.153.55:8080${endpoint}`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(formData)
            });
            const data = await response.json();

            if (response.ok) {
                localStorage.setItem("token", data.token);
                localStorage.setItem("username", data.username);
                onLogin(data);
            } else {
                alert(data.message || "Authentication failed");
            }
        } catch (error) {
            console.error("Error:", error);
            alert("An error occurred. Please try again.");
        }
    };

    const handleGoogleLogin = () => {
        window.location.href = 'http://91.228.153.55:8080/oauth2/authorization/google';
    };

    return (
        <div style={{ maxWidth: '400px', margin: '50px auto', padding: '20px' }}>
            <h2>{isLogin ? 'Login' : 'Register'}</h2>

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={formData.username}
                        onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                        required
                        style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
                    />
                </div>

                {!isLogin && (
                    <div style={{ marginBottom: '15px' }}>
                        <input
                            type="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                            required
                            style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
                        />
                    </div>
                )}

                <div style={{ marginBottom: '15px' }}>
                    <input
                        type="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                        required
                        style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
                    />
                </div>

                <button
                    type="submit"
                    style={{ width: '100%', padding: '10px', backgroundColor: '#007bff', color: 'white', border: 'none', marginBottom: '10px' }}
                >
                    {isLogin ? 'Login' : 'Register'}
                </button>
            </form>

            <button
                onClick={handleGoogleLogin}
                style={{
                    width: '100%',
                    padding: '10px',
                    backgroundColor: '#db4437',
                    color: 'white',
                    border: 'none',
                    marginBottom: '10px'
                }}
            >
                üîç Continue with Google
            </button>

            <p>
                {isLogin ? "Don't have an account? " : "Already have an account? "}
                <button
                    type="button"
                    onClick={() => setIsLogin(!isLogin)}
                    style={{ background: 'none', border: 'none', color: '#007bff', cursor: 'pointer' }}
                >
                    {isLogin ? 'Register' : 'Login'}
                </button>
            </p>
        </div>
    );
};
}

export default Auth;
