import React, { useState } from 'react';
import './Auth.css';

const Auth = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isLogin ? '/api/auth/login' : '/api/auth/register';
    
    try {
      const response = await fetch(`http://91.228.153.55:8080${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      
      if (response.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('username', data.username);
        onLogin(data);
      } else {
        alert(data.message || 'Authentication failed');
      }
    } catch (error) {
      console.error('Auth error:', error);
      alert('Network error');
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = 'http://91.228.153.55:8080/oauth2/authorization/google';
  };

  return (
    <div className="auth-container">
      <div className="auth-header">
        <h1>{isLogin ? 'Welcome Back!' : 'Join Us!'}</h1>
        <p>{isLogin ? 'Sign in to your account' : 'Create your account'}</p>
      </div>
      
      <div className="auth-form-container">
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="auth-input-section">
            <input
              type="text"
              placeholder="Username"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              required
              className="auth-input"
            />
          </div>
          
          {!isLogin && (
            <div className="auth-input-section">
              <input
                type="email"
                placeholder="Email"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
                className="auth-input"
              />
            </div>
          )}
          
          <div className="auth-input-section">
            <input
              type="password"
              placeholder="Password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              required
              className="auth-input"
            />
          </div>
          
          <button type="submit" className="auth-btn primary">
            {isLogin ? 'Sign In' : 'Create Account'}
          </button>
        </form>

        <div className="auth-divider">
          <span>or</span>
        </div>

        <button onClick={handleGoogleLogin} className="auth-btn google">
          <span className="google-icon">🔍</span>
          Continue with Google
        </button>

        <div className="auth-switch">
          <p>
            {isLogin ? "Don't have an account? " : "Already have an account? "}
            <button 
              type="button"
              onClick={() => setIsLogin(!isLogin)}
              className="auth-link"
            >
              {isLogin ? 'Sign up' : 'Sign in'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Auth;
